validator.add(userName, [{
    stratege: 'isNonEmpty',
    errMsg: '用户名不能为空'
}]);

validator.add(phone, [{
    stratege: 'isNonEmpty',
    errMsg: '手机号不能为空'
}, {
    stratege: 'length:11',
    errMsg: '手机号为11位'
}]);


